    @valid_attributes = {
      :question_id => @question_response.question_id,
      :user_id => @question_response.user_id,
      :response => @question_response.response
    }
  end

  describe "GET index" do
    it "assigns all question_responses as @question_responses" do
      get :index
      assigns(:question_responses).should eq(QuestionResponse.all)
    end
  end

  describe "GET show" do
    it "assigns the requested question_response as @question_response" do
      get :show, :id => @question_response.id
