  describe "GET new" do
    it "assigns a new question_response as @question_response" do
      get :new
      assigns(:question_response).should be_a_new(QuestionResponse)
    end
  end

  describe "GET edit" do
    it "assigns the requested question_response as @question_response" do
      get :edit, :id => @question_response.id
      assigns(:question_response).should eq(@question_response)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new QuestionResponse" do
        expect {
          post :create, :question_response => @valid_attributes
        }.to change(QuestionResponse, :count).by(1)
      end

      it "assigns a newly created question_response as @question_response" do
        post :create, :question_response => @valid_attributes
        assigns(:question_response).should be_a(QuestionResponse)
        assigns(:question_response).should be_persisted
      end

      it "redirects to the created question_response" do
        post :create, :question_response => @valid_attributes
        response.should redirect_to(QuestionResponse.last)
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved question_response as @question_response" do
        # Trigger the behavior that occurs when invalid params are submitted
        QuestionResponse.any_instance.stub(:save).and_return(false)
        post :create, :question_response => {}
        assigns(:question_response).should be_a_new(QuestionResponse)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        QuestionResponse.any_instance.stub(:save).and_return(false)
        post :create, :question_response => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested question_response" do
        # Assuming there are no other question_responses in the database, this
        # specifies that the QuestionResponse created on the previous line
        # receives the :update_attributes message with whatever params are
